import { Text, View, StyleSheet, TextInput, Pressable } from 'react-native';
import React from 'react';
import { Appbar, Card } from 'react-native-paper';

const Chatbot = () => {
  return(
    <View>
      <Appbar.Header style={styles.appbar}>
        <Text 
          style={{fontFamily: 'Aleo-Regular', fontSize: 25, color: '#ffffff', textAlign: 'center'}}
        >Chatbot</Text>
      </Appbar.Header>

      <View style={styles.chatbar}>
        <Card style={styles.smsBar}>
          <TextInput></TextInput>
        </Card>
        <Pressable style={styles.sendBtn}> 
          <Text style={{textAlign: 'center'}}>Send</Text>
        </Pressable>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({

  appbar:{
    backgroundColor: '#98BF64',
    margin: 'auto',
  },

  chatbar:{
    position: 'absolute',
    bottom: -505,
    left: 0,
    right: 0,
    backgroundColor: '#98BF64',
    padding: 10,
    display: 'flex',
    flexDirection: 'row',
    gap: 5,
  },

  smsBar:{
    borderRadius: 30,
    width: 250,
    padding: 5
  },

  sendBtn:{
    backgroundColor: '#F6E7A8',
    padding: 5,
    borderRadius: 30,
    width: 60,
  }


});

export default Chatbot;
