import React from 'react';
import { View, Text, Button, Image, TouchableOpacity, StyleSheet, Dimensions } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const Screen1 = () => {
  const navigation = useNavigation();
  const navigateToHome = () => {
    navigation.navigate('Home');
  };
  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.loginButton} onPress={() => navigation.navigate('Signup')}>
        <Text style={styles.loginButtonText}>Login</Text>
      </TouchableOpacity>
      <Image source={require('../assets/images/screen1.png')} style={styles.image} resizeMode="contain" />
      <Text style={styles.subtitleText}>Healthy food meal options to choose from{'\n'}that suit your diet and appetite.</Text>
      <TouchableOpacity style={[styles.planMealButton, { width: Dimensions.get('window').width * 0.6 }]} onPress={navigateToHome}>
        <Text style={styles.buttonText}>Plan Meal</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 10,
    padding: 20,
    backgroundColor: '#98bf64',
  },
  image: {
    width: Dimensions.get('window').width * 0.8, // Adjusted width to fit the screen width
    height: Dimensions.get('window').width * 0.8, // Adjusted height to maintain aspect ratio
  },
  subtitleText: {
    fontSize: 16,
    marginTop: 20,
    marginBottom: 40,
    textAlign: 'center',
    color: '#ffffff',
  },
  planMealButton: {
    backgroundColor: '#e67f22',
    paddingVertical: 10,
    paddingHorizontal: 40,
    borderRadius: 5,
    marginBottom: 10,
  },
  buttonText: {
    fontSize: 16,
    color: '#ffffff',
    textAlign: 'center',
  },
  loginButton: {
    position: 'absolute',
    top: 20,
    right: 20,
    padding: 10,
    backgroundColor: '#be4a31',
    borderRadius: 5,
    marginTop: 20,
  },
  loginButtonText: {
    fontSize: 16,
    color: '#FFFFFF',
  },
});

export default Screen1;
