import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, TextInput, Modal } from 'react-native';
import { useFonts } from 'expo-font';
import { useNavigation } from '@react-navigation/native';


export default function Signup() {
  const [region, setRegion] = useState('NCR');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [modalVisible, setModalVisible] = useState(false);
  const navigation = useNavigation();
  const navigateToVerification = () => {
    navigation.navigate('Verification');
  };
  const navigateToLogin = () => {
    navigation.navigate('Login');
  };

  const handleBackButtonPress = () => {
    navigation.goBack(); // Navigate back to the previous screen
  };

  const [fontsLoaded] = useFonts({
    'Aleo-Regular': require('../assets/fonts/Aleo-Regular.ttf'),
  });

  if (!fontsLoaded) {
    return null; // Wait for the fonts to load
  }

  const regions = ["NCR", "Region 1", "Region 2", "Region 3", "Region 4"];
  

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity>
          <Text style={[styles.headerText, { fontFamily: 'Aleo_400Regular' }]}
          onPress={handleBackButtonPress}>Cancel</Text>
        </TouchableOpacity>
        <Text style={[styles.headerText, styles.headerTitle, { fontFamily: 'Aleo_400Regular' }]}>Sign Up</Text>
        <TouchableOpacity>
          <Text style={[styles.headerText, { fontFamily: 'Aleo_400Regular' }]}> </Text>
        </TouchableOpacity>
      </View>
      <View style={styles.formContainer}>
        <Text style={[styles.label, { fontFamily: 'Aleo_400Regular' }]}>Select your region</Text>
        <TouchableOpacity
          style={styles.dropdownButton}
          onPress={() => setModalVisible(true)}
        >
          <Text style={styles.dropdownButtonText}>{region}</Text>
        </TouchableOpacity>
        <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible}
          onRequestClose={() => {
            setModalVisible(false);
          }}
        >
          <View style={styles.centeredView}>
            <View style={styles.modalView}>
              {regions.map((item, index) => (
                <TouchableOpacity
                  key={index}
                  style={styles.modalOption}
                  onPress={() => {
                    setRegion(item);
                    setModalVisible(false);
                  }}
                >
                  <Text style={styles.modalOptionText}>{item}</Text>
                </TouchableOpacity>
              ))}
            </View>
          </View>
        </Modal>
        <Text style={[styles.label, { fontFamily: 'Aleo_400Regular' }]}>Phone Number</Text>
        <TextInput
          style={[styles.input, { fontFamily: 'Aleo_400Regular' }]}
          onChangeText={(text) => setPhoneNumber(text)}
          value={phoneNumber}
          keyboardType="phone-pad"
        />
        <Text style={[styles.orText, { fontFamily: 'Aleo_400Regular' }]}>or</Text>
        <TouchableOpacity style={[styles.button, styles.googleButton]}>
          <Text style={[styles.buttonText, { fontFamily: 'Aleo_400Regular' }]}>Continue with Google</Text>
        </TouchableOpacity>
        <TouchableOpacity style={[styles.button, styles.facebookButton]}>
          <Text style={[styles.buttonText, { fontFamily: 'Aleo_400Regular' }]}>Continue with Facebook</Text>
        </TouchableOpacity>
        
        <TouchableOpacity style={[styles.button, styles.loginButton]}
        onPress={navigateToVerification}>
          <Text style={styles.buttonText}>Sign Up</Text>
        </TouchableOpacity>

         <Text style={[styles.accountText, { fontFamily: 'Aleo_400Regular' }]}>Already have an account?</Text>
         <TouchableOpacity onPress={navigateToLogin}>
          <Text style={[styles.accountText, { fontFamily: 'Aleo_400Regular' }]}>
          Login</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.footer}>
        <Text style={{ color: 'transparent' }}>Footer</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FBD28552',
    alignItems: 'center',
    justifyContent: 'center',
    width: '100%',
  },
header: {
  flexDirection: 'row',
  justifyContent: 'space-between',
  alignItems: 'center',
  backgroundColor: '#98BF64',
  paddingHorizontal: 20,
  paddingVertical: 5,
  width: '100%',
  height: 100,
  position: 'absolute',
  top: 0,
  left: 0,
  right: 0,
},

  headerText: {
    color: '#FFFFFF',
    fontSize: 18,
  },
  formContainer: {
    width: '80%',
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
    color: '#AF5F17',
  },
  dropdownButton: {
    borderWidth: 1,
    borderColor: '#AF5F17',
    borderRadius: 5,
    marginBottom: 20,
    backgroundColor: '#FAFFC7',
    justifyContent: 'center',
    alignItems: 'center',
    height: 40,
    paddingHorizontal: 10,
  },
  dropdownButtonText: {
    color: '#AF5F17',
    fontFamily: 'Aleo_400Regular',
  },
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  modalOption: {
    marginBottom: 15,
  },
  modalOptionText: {
    fontSize: 16,
    color: '#AF5F17',
    fontFamily: 'Aleo_400Regular',
  },
  input: {
    height: 40,
    borderWidth: 1,
    borderColor: '#AF5F17',
    borderRadius: 5,
    paddingHorizontal: 10,
    marginBottom: 20,
    backgroundColor: '#FAFFC7',
    color: '#AF5F17',
  },
  orText: {
    textAlign: 'center',
    marginBottom: 20,
    color: '#AF5F17',
  },
  button: {
    height: 40,
    borderRadius: 5,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 30,
  },
  googleButton: {
    backgroundColor: '#DB4437',
  },
  facebookButton: {
    backgroundColor: '#3B5998',
  },
  loginButton: {
    backgroundColor: '#FF9800',
    paddingVertical: 20,
  },
  buttonText: {
    color: '#ffffff',
  },
  accountText: {
    textAlign: 'center',
    marginBottom: 10,
    color: '#AF5F17',
  },
  footer: {
    backgroundColor: '#98BF64',
    paddingVertical: 20,
    width: '100%',
    position: 'absolute',
    bottom: 0,
  },
  headerTitle: {
    position: 'absolute',
    left: '50%',
    transform: [{ translateX: -50 }],
  },
});
